function PopupWithForm({popup, title,isOpen, form, children }) {
    const popupIsOpen = isOpen ? 'popup_opened' : '';
    return (
        <div className={`popup ${popup} ${popupIsOpen}`}>

        <div className="popup__container">
          <button className="popup__close" type="reset" aria-label="Закрыть"></button>
          <h2 className="popup__title">{title}</h2>
          <form className="popup__form popup__form_profile" method="post"   name ={`form-${form}`} noValidate>
          {children}
            <button type="submit" className="popup__input-save">Сохранить</button>
          </form>
        </div>
      </div>

    )

}

export default PopupWithForm;
