import logo from '../images/Vector.svg';
import '../index.css';
import React from "react";
import Header from './Header'
import Footer from './Footer'
import Main from './Main'
import PopupWithForm from './PopupWithForm'
import ImagePopup from './ImagePopup'

function App() {

 {/* function handleEditProfileClick() {
const buttonProfile = document.querySelector('.profile__edit-button');
const popup = document.querySelector('.popup_edit');
buttonProfile.addEventListener('click', () => {
popup.classList.add('popup_opened')
})
  }
function handleAddPlaceClick() {
  const buttonAdd = document.querySelector('.profile__add-button');
  const popupAdd = document.querySelector('.popup_add');
  buttonAdd .addEventListener('click', () => {
  popupAdd.classList.add('popup_opened')
  })
}

  function handleEditAvatarClick() {
    const buttonAvatar = document.querySelector('.profile__avatar-btn');
    const popupAvatar = document.querySelector('.popup_avatar');
    buttonAvatar.addEventListener('click', () => {
      popupAvatar.classList.add('popup_opened')
    })
  } */}
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  return (
    <div className="page">
   <Header />
   <Main

    onEditProfile={handleEditProfileClick}
    onAddPlace={handleAddPlaceClick}
    onEditAvatar={handleEditAvatarClick}

   />
   <Footer />     

  <PopupWithForm

  />
<ImagePopup/>
  <div className="popup popup_edit">
    <div className="popup__container">
      <button className="popup__close" type="reset" aria-label="Закрыть"></button>
      <h2 className="popup__title">Редактировать профиль</h2>
      <form className="popup__form popup__form_profile" method="post" name="form-profile" noValidate>
        <input id="name" type="text" className="popup__input popup__input_data_name" required name="name" placeholder="Имя"
          minLength="2" maxLength="40"></input>
        <span id="name-error" className="popup__error name-error"></span>
        <input id="info" type="text" className="popup__input popup__input_data_info" required name="info"
          placeholder="Информация о себе" minLength="2" maxLength="200"></input>
        <span id="info-error" className="popup__error info-error"></span>
        <button type="submit" className="popup__input-save">Сохранить</button>
      </form>
    </div>
  </div>
  <div className="popup popup_add">
    <div className="popup__container">
      <button className="popup__close popup__close_add" type="reset" aria-label="Закрыть"></button>
      <h2 className="popup__title">Новое место</h2>
      <form className="popup__form popup__form_card" method="post" name="form-card" noValidate>
        <input id="title" type="text" className="popup__input popup__input_data_title" required name="name"
          placeholder="Название" minLength="2" maxLength="30"></input>
        <span id="title-error" className="popup__error title-error"></span>

        <input id="url" type="url" className="popup__input popup__input_data_url" required name="link"
          placeholder="Ссылка на картинку"></input>
        <span id="url-error" className="popup__error url-error"></span>

        <button type="submit" className="popup__input-save popup__input-add popup__input-save_disabled">Создать</button>
      </form>
    </div>
  </div>

  <div className="popup popup_delete">
    <div className="popup__container">
      <button className="popup__close" type="button" aria-label="Закрыть"></button>
      <h2 className="popup__title">Вы уверены?</h2>
      <form className="popup__form popup__form_delete" id="form__delete">
        <button className="popup__input-save" type="submit" aria-label="Да">Да</button></form>
    </div>
  </div>

  <div className="popup popup_avatar">
    <div className="popup__container">
      <button className="popup__close" type="button" aria-label="Закрыть"> </button>
      <h2 className="popup__title">Обновить аватар</h2>
      <form className="popup__form popup__form_avatar" id="form__avatar" noValidate>

        <input id="url-avatar" type="url" className="popup__input popup__input_data_url popup__input_link" name="avatar"
          placeholder="Ссылка на картинку" required></input>
          <span id="url-avatar-error" className="popup__error url-avatar-error"></span>

        <button className="popup__input-save" type="submit" aria-label="Сохранить">Сохранить</button>
      </form>
    </div>
  </div>

  <template id="card">
    <article className="place">
      <div className="place__image">
        <img className="place__photo"></img>
      </div>
      <div className="place__info">
        <h2 className="place__title" id=""></h2>
        <div className="place__like_container">
          <button className="place__like" type="button" aria-label="Нравится фотография"></button>
          <p className="place__like_amount"></p>
        </div>
        <button className="place__delete" type="button" aria-label="Удалить фотографию"></button>
      </div>
    </article>
  </template>
    </div>
  );
}

export default App;
